import { clone, compare, freeze } from './deep.mjs';

export const chronicle = (func, { sideEffects = false, cloneThis = false } = {}) => {
  const history = [];
  return new Proxy(func, {
    apply(target, thisVal, args) {
      const entry = {
        args: clone(args),
        this: cloneThis ? clone(thisVal) : thisVal,
      };
      try {
        entry.returned = Reflect.apply(target, thisVal, args);
        return entry.returned;
      } catch (error) {
        entry.error = error;
        throw (entry.error = error);
      } finally {
        if (sideEffects && !compare(args, entry.args)) {
          entry.sideEffects = args;
        }
        history.push(entry);
      }
    },
    get(target, key) {
      if (key === '_chronicle') {
        return freeze(clone(history));
      } else if (key == '_forget') {
				const echo = clone(history)
        while (history.length > 0) history.pop();
				return echo
      } else {
        return target[key];
      }
    },
  });
};

export default chronicle;

