export const deepClone = (src, clonesMap = new Map()) => {
  if (Object(src) !== src || typeof src === 'function') return src;

  if (clonesMap.has(src)) return clonesMap.get(src);

  if (src instanceof Date) new Date(src.getTime());

  if (src instanceof RegExp) return new RegExp(src);

  if (Array.isArray(src)) {
    const clone = src.map((item) => deepClone(item, clonesMap));
    clonesMap.set(src, clone);
    return clone;
  }

  if (src instanceof Error) {
    const newError = new src.constructor(src.message);
    newError.stack = src.stack;
    return newError;
  }

  if (src instanceof Object) {
    const clone = Object.create(src.__proto__);
    clonesMap.set(src, clone);
    for (const key in src) clone[key] = deepClone(src[key], clonesMap);
    return clone;
  }

  return src;
};

export default deepClone;

