export const deepFreeze = (thing) => {
  if (Array.isArray(thing)) {
    return Object.freeze(thing.map((item) => deepFreeze(item)));
  }

  if (Object(thing) === thing) {
    return Object.freeze(
      Object.entries(thing).reduce(
        (all, [key, value]) => ({ ...all, [key]: deepFreeze(value) }),
        {},
      ),
    );
  }

  return thing;
};

export default deepFreeze;

